
  var buf = {
    low: new Buffer(8),
    high: new Buffer(8),
  };
  buf.low.fill(0);
  buf.high.fill(0);


  var a = function(data, frame) {
    var doIt = function(b, frame) {
      var bufferPosition = frame / 8 | 0;
      var tmp = b.readUInt8(bufferPosition);

      tmp |= 1<<(frame % 8)

      b.writeUInt8(tmp, bufferPosition);
    };
    if (frame < 64) {
      doIt(data.low, frame);
    } else {
      doIt(data.high, frame - 64);
    }
  }
